import type { Express } from "express";
import { createServer, type Server } from "http";
import { WebSocketServer } from "ws";
import { storage } from "./storage";
import { insertEnergyDataSchema, insertNotificationSchema } from "@shared/schema";

export async function registerRoutes(app: Express): Promise<Server> {
  const httpServer = createServer(app);

  // WebSocket server for real-time data on a different path to avoid conflicts
  const wss = new WebSocketServer({ 
    server: httpServer,
    path: '/energy-ws' // Use a specific path to avoid conflicts with Vite HMR
  });

  // Simulate real-time energy data updates
  setInterval(() => {
    const realtimeData = {
      activePower: Math.random() * 100 + 200,
      reactivePower: Math.random() * 50 + 200,
      voltage: 220 + Math.random() * 10,
      powerFactor: 0.4 + Math.random() * 0.2,
      timestamp: new Date().toISOString(),
    };

    wss.clients.forEach((client) => {
      if (client.readyState === 1) { // WebSocket.OPEN
        client.send(JSON.stringify({ type: 'energy-update', data: realtimeData }));
      }
    });
  }, 3000); // Update every 3 seconds

  // Energy data routes
  app.get("/api/energy/latest/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const latestData = await storage.getLatestEnergyData(userId);
      res.json(latestData);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch latest energy data" });
    }
  });

  app.get("/api/energy/history/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const limit = req.query.limit ? parseInt(req.query.limit as string) : 50;
      const energyData = await storage.getEnergyData(userId, limit);
      res.json(energyData);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch energy history" });
    }
  });

  app.post("/api/energy", async (req, res) => {
    try {
      const validatedData = insertEnergyDataSchema.parse(req.body);
      const energyData = await storage.createEnergyData(validatedData);
      res.json(energyData);
    } catch (error) {
      res.status(400).json({ error: "Invalid energy data" });
    }
  });

  // Billing data routes
  app.get("/api/billing/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const billingData = await storage.getBillingData(userId);
      res.json(billingData);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch billing data" });
    }
  });

  // Notifications routes
  app.get("/api/notifications/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const notifications = await storage.getNotifications(userId);
      res.json(notifications);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch notifications" });
    }
  });

  app.post("/api/notifications", async (req, res) => {
    try {
      const validatedData = insertNotificationSchema.parse(req.body);
      const notification = await storage.createNotification(validatedData);
      res.json(notification);
    } catch (error) {
      res.status(400).json({ error: "Invalid notification data" });
    }
  });

  app.patch("/api/notifications/:id/read", async (req, res) => {
    try {
      const { id } = req.params;
      await storage.markNotificationAsRead(id);
      res.json({ success: true });
    } catch (error) {
      res.status(500).json({ error: "Failed to mark notification as read" });
    }
  });

  // User settings routes
  app.get("/api/settings/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const settings = await storage.getUserSettings(userId);
      res.json(settings);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch user settings" });
    }
  });

  app.patch("/api/settings/:userId", async (req, res) => {
    try {
      const { userId } = req.params;
      const settings = await storage.updateUserSettings(userId, req.body);
      res.json(settings);
    } catch (error) {
      res.status(500).json({ error: "Failed to update user settings" });
    }
  });

  // User routes
  app.get("/api/user/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const user = await storage.getUser(id);
      if (!user) {
        return res.status(404).json({ error: "User not found" });
      }
      res.json(user);
    } catch (error) {
      res.status(500).json({ error: "Failed to fetch user" });
    }
  });

  // Support contact route
  app.post("/api/support/contact", async (req, res) => {
    try {
      const { subject, message, userId } = req.body;

      // In a real application, this would send an email or create a support ticket
      // For now, we'll create a notification
      const notification = await storage.createNotification({
        userId,
        title: "Support Request Received",
        message: `Your support request about "${subject}" has been received. We'll get back to you soon.`,
        type: "info",
      });

      res.json({ success: true, notification });
    } catch (error) {
      res.status(500).json({ error: "Failed to submit support request" });
    }
  });

  return httpServer;
}

server/storage.ts.content:
import { users, energyData, billingData, notifications, userSettings, type User, type InsertUser, type EnergyData, type InsertEnergyData, type BillingData, type InsertBillingData, type Notification, type InsertNotification, type UserSettings, type InsertUserSettings } from "@shared/schema";
import { db } from "./db";
import { eq, desc, sql } from "drizzle-orm";
import { randomUUID } from "crypto";

export interface IStorage {
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  getEnergyData(userId: string, limit?: number): Promise<EnergyData[]>;
  createEnergyData(data: InsertEnergyData): Promise<EnergyData>;
  getLatestEnergyData(userId: string): Promise<EnergyData | undefined>;
  getBillingData(userId: string): Promise<BillingData | undefined>;
  createBillingData(data: InsertBillingData): Promise<BillingData>;
  getNotifications(userId: string): Promise<Notification[]>;
  createNotification(notification: InsertNotification): Promise<Notification>;
  markNotificationAsRead(id: string): Promise<void>;
  getUserSettings(userId: string): Promise<UserSettings | undefined>;
  createUserSettings(settings: InsertUserSettings): Promise<UserSettings>;
  updateUserSettings(userId: string, settings: Partial<UserSettings>): Promise<UserSettings>;
}

export class MemStorage implements IStorage {
  private users: Map<string, User>;
  private energyData: Map<string, EnergyData[]>;
  private billingData: Map<string, BillingData>;
  private notifications: Map<string, Notification[]>;
  private userSettings: Map<string, UserSettings>;

  constructor() {
    this.users = new Map();
    this.energyData = new Map();
    this.billingData = new Map();
    this.notifications = new Map();
    this.userSettings = new Map();

    // Initialize with demo user and data
    this.initializeDemoData();
  }

  private initializeDemoData() {
    const demoUserId = "demo-user-1";
    const demoUser: User = {
      id: demoUserId,
      username: "inayat",
      email: "inayatalikatif@gmail.com",
      name: "Inayat Ali",
      password: "demo123",
      createdAt: new Date(),
    };

    this.users.set(demoUserId, demoUser);

    // Initialize energy data
    const energyDataPoints: EnergyData[] = [];
    for (let i = 0; i < 100; i++) {
      energyDataPoints.push({
        id: randomUUID(),
        userId: demoUserId,
        timestamp: new Date(Date.now() - i * 60000), // Every minute for last 100 minutes
        activePower: (Math.random() * 100 + 200).toString(),
        reactivePower: (Math.random() * 50 + 200).toString(),
        voltage: (220 + Math.random() * 10).toString(),
        powerFactor: (0.4 + Math.random() * 0.2).toString(),
        consumption: (Math.random() * 5 + 10).toString(),
      });
    }
    this.energyData.set(demoUserId, energyDataPoints);

    // Initialize billing data
    const billing: BillingData = {
      id: randomUUID(),
      userId: demoUserId,
      period: "01 Jul - 31 Aug 2025",
      currentBill: "57.00",
      energyUsed: "129.5",
      contract: "Bioraria (F1/F23)",
      estimatedFinalBill: "83.40",
      co2Emitted: "42.7",
      co2Avoided: "12.1",
    };
    this.billingData.set(demoUserId, billing);

    // Initialize notifications
    const notificationsList: Notification[] = [
      {
        id: randomUUID(),
        userId: demoUserId,
        title: "Energy Usage Alert",
        message: "Your consumption is 15% higher than usual today.",
        type: "warning",
        read: false,
        createdAt: new Date(Date.now() - 7200000), // 2 hours ago
      },
      {
        id: randomUUID(),
        userId: demoUserId,
        title: "Savings Opportunity",
        message: "Switch to Green Energy Plan and save â‚¬8.80 monthly.",
        type: "success",
        read: false,
        createdAt: new Date(Date.now() - 86400000), // 1 day ago
      },
    ];
    this.notifications.set(demoUserId, notificationsList);

    // Initialize user settings
    const settings: UserSettings = {
      id: randomUUID(),
      userId: demoUserId,
      energyAlerts: true,
      billingReminders: true,
      savingsTips: false,
      systemUpdates: true,
    };
    this.userSettings.set(demoUserId, settings);
  }

  async getUser(id: string): Promise<User | undefined> {
    return this.users.get(id);
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    return Array.from(this.users.values()).find(
      (user) => user.username === username,
    );
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const id = randomUUID();
    const user: User = { ...insertUser, id, createdAt: new Date() };
    this.users.set(id, user);
    return user;
  }

  async getEnergyData(userId: string, limit = 50): Promise<EnergyData[]> {
    const data = this.energyData.get(userId) || [];
    return data.slice(0, limit).sort((a, b) => 
      new Date(b.timestamp!).getTime() - new Date(a.timestamp!).getTime()
    );
  }

  async createEnergyData(data: InsertEnergyData): Promise<EnergyData> {
    const id = randomUUID();
    const energyDataPoint: EnergyData = { 
      ...data, 
      id, 
      timestamp: new Date(),
      activePower: data.activePower || "245",
      reactivePower: data.reactivePower || "246",
      voltage: data.voltage || "220",
      powerFactor: data.powerFactor || "0.5",
      consumption: data.consumption || "14.7",
    };

    const userEnergyData = this.energyData.get(data.userId) || [];
    userEnergyData.unshift(energyDataPoint);
    // Keep only last 100 records
    if (userEnergyData.length > 100) {
      userEnergyData.pop();
    }
    this.energyData.set(data.userId, userEnergyData);

    return energyDataPoint;
  }

  async getLatestEnergyData(userId: string): Promise<EnergyData | undefined> {
    const data = this.energyData.get(userId) || [];
    return data[0];
  }

  async getBillingData(userId: string): Promise<BillingData | undefined> {
    return this.billingData.get(userId);
  }

  async createBillingData(data: InsertBillingData): Promise<BillingData> {
    const id = randomUUID();
    const billing: BillingData = { 
      ...data, 
      id,
      currentBill: data.currentBill || "0.00",
      energyUsed: data.energyUsed || "0.00",
      contract: data.contract || "Standard",
      estimatedFinalBill: data.estimatedFinalBill || "0.00",
      co2Emitted: data.co2Emitted || "0.00",
      co2Avoided: data.co2Avoided || "0.00",
    };
    this.billingData.set(data.userId, billing);
    return billing;
  }

  async getNotifications(userId: string): Promise<Notification[]> {
    const notifications = this.notifications.get(userId) || [];
    return notifications.sort((a, b) => 
      new Date(b.createdAt!).getTime() - new Date(a.createdAt!).getTime()
    );
  }

  async createNotification(notification: InsertNotification): Promise<Notification> {
    const id = randomUUID();
    const newNotification: Notification = { 
      ...notification, 
      id, 
      read: notification.read || false,
      createdAt: new Date() 
    };

    const userNotifications = this.notifications.get(notification.userId) || [];
    userNotifications.unshift(newNotification);
    this.notifications.set(notification.userId, userNotifications);

    return newNotification;
  }

  async markNotificationAsRead(id: string): Promise<void> {
    for (const [userId, notifications] of Array.from(this.notifications.entries())) {
      const notification = notifications.find((n: any) => n.id === id);
      if (notification) {
        notification.read = true;
        break;
      }
    }
  }

  async getUserSettings(userId: string): Promise<UserSettings | undefined> {
    return this.userSettings.get(userId);
  }

  async createUserSettings(settings: InsertUserSettings): Promise<UserSettings> {
    const id = randomUUID();
    const userSettings: UserSettings = { 
      ...settings, 
      id,
      energyAlerts: settings.energyAlerts ?? true,
      billingReminders: settings.billingReminders ?? true,
      savingsTips: settings.savingsTips ?? false,
      systemUpdates: settings.systemUpdates ?? true,
    };
    this.userSettings.set(settings.userId, userSettings);
    return userSettings;
  }

  async updateUserSettings(userId: string, updates: Partial<UserSettings>): Promise<UserSettings> {
    const existing = this.userSettings.get(userId);
    if (!existing) {
      throw new Error("User settings not found");
    }

    const updated = { ...existing, ...updates };
    this.userSettings.set(userId, updated);
    return updated;
  }
}

export class DatabaseStorage implements IStorage {
  async getUser(id: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.id, id));
    return user || undefined;
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const [user] = await db.select().from(users).where(eq(users.username, username));
    return user || undefined;
  }

  async createUser(insertUser: InsertUser): Promise<User> {
    const [user] = await db
      .insert(users)
      .values(insertUser)
      .returning();
    return user;
  }

  async getEnergyData(userId: string, limit: number = 100): Promise<EnergyData[]> {
    return await db
      .select()
      .from(energyData)
      .where(eq(energyData.userId, userId))
      .orderBy(desc(energyData.timestamp))
      .limit(limit);
  }

  async createEnergyData(data: InsertEnergyData): Promise<EnergyData> {
    const [energy] = await db
      .insert(energyData)
      .values(data)
      .returning();
    return energy;
  }

  async getLatestEnergyData(userId: string): Promise<EnergyData | undefined> {
    const [latest] = await db
      .select()
      .from(energyData)
      .where(eq(energyData.userId, userId))
      .orderBy(desc(energyData.timestamp))
      .limit(1);
    return latest || undefined;
  }

  async getBillingData(userId: string): Promise<BillingData | undefined> {
    const [billing] = await db
      .select()
      .from(billingData)
      .where(eq(billingData.userId, userId))
      .orderBy(desc(billingData.id))
      .limit(1);
    return billing || undefined;
  }

  async createBillingData(data: InsertBillingData): Promise<BillingData> {
    const [billing] = await db
      .insert(billingData)
      .values(data)
      .returning();
    return billing;
  }

  async getNotifications(userId: string): Promise<Notification[]> {
    return await db
      .select()
      .from(notifications)
      .where(eq(notifications.userId, userId))
      .orderBy(desc(notifications.createdAt));
  }

  async createNotification(notification: InsertNotification): Promise<Notification> {
    const [notif] = await db
      .insert(notifications)
      .values(notification)
      .returning();
    return notif;
  }

  async markNotificationAsRead(id: string): Promise<void> {
    await db
      .update(notifications)
      .set({ read: true })
      .where(eq(notifications.id, id));
  }

  async getUserSettings(userId: string): Promise<UserSettings | undefined> {
    const [settings] = await db
      .select()
      .from(userSettings)
      .where(eq(userSettings.userId, userId));
    return settings || undefined;
  }

  async createUserSettings(settings: InsertUserSettings): Promise<UserSettings> {
    const [userSetting] = await db
      .insert(userSettings)
      .values(settings)
      .returning();
    return userSetting;
  }

  async updateUserSettings(userId: string, settingsUpdate: Partial<UserSettings>): Promise<UserSettings> {
    const [updated] = await db
      .update(userSettings)
      .set(settingsUpdate)
      .where(eq(userSettings.userId, userId))
      .returning();
    return updated;
  }
}

export const storage = new DatabaseStorage();

